INSERT INTO `notice` (`notice_id`, `topic`, `info`, `author`, `ward_no`, `date`, `status`) VALUES
(20, 'কোভিড ভ্যাক্সিন টিকা কার্যক্রম', 'স্বাস্থ্য অধিদপ্তর এখন বলছে গ্রাম বা ইউনিয়ন পর্যায়ের টিকা কার্যক্রম ১৪ই অগাস্ট শুরু হবে, এবং আগের নির্ধারিত তারিখ ৭ই অগাস্টে সারাদেশে পরীক্ষামূলক টিকা দেয়া হবে এতদিন স্বাস্থ্যমন্ত্রীসহ সরকারের অনেক মন্ত্রী ৭ই অগাস্ট থেকে সাতদিনে এক কোটি টিকা দেয়ার কথা বলেছেন।', 'Counsilor', 1, '2021-12-29', 'approved'),
(21, 'মশক নিধন ও ডেঙ্গু মোকাবেলায় একগুচ্ছ পদক্ষেপ', 'স্থানীয় সরকার মন্ত্রী মো. তাজুল ইসলামের সভাপতিত্বে বৃহস্পতিবার সচিবালয়ে মশক নিধন ও ডেঙ্গু প্রতিরোধ সংক্রান্ত এক আন্তঃমন্ত্রণালয় সভায় একগুচ্ছ সিদ্ধান্ত হয়েছে বলে মন্ত্রণালয়ের এক সংবাদ বিজ্ঞপ্তিতে জানানো হয়েছে।\r\nমন্ত্রীকে উদ্ধৃত করে বিজ্ঞপ্তিতে বলা হয়, “সরকারি ভবন, লেক, পার্ক, খাল রক্ষণাবেক্ষণের দায়িত্ব পালন করবে সংশ্লিষ্ট মন্ত্রণালয়, অধিদপ্তর বা কর্তৃপক্ষ। আর মশা মারার কাজ করবে সিটি করপোরেশন।', 'Counsilor', 2, '2021-12-30', 'approved'),
(23, 'ভোটার হওয়ার নতুন নিয়ম ২০২২: এনআইডি (NID) করতে যা যা লাগবে', 'বাংলাদেশের সকল নাগরিকদের জন্য পরিচয় সনদ হচ্ছে ভোটার কার্ড যাকে জাতীয় পরিচয় পত্র বা সংক্ষেপে এনআইডি বলা হয়। দেশের ভিতর এটি পরিচয় প্রমাণের প্রধান মাধ্যম। আঠার বছর পূর্ণ হলে ভোটার আইডি করা সবার জন্য বাধ্যতা মূলক করা হয়েছে আইনে।', 'Counsilor', 1, '2021-12-30', 'approved'),
(24, 'শীতার্ত ব্যক্তিদের জন্য শীতবস্ত্র', 'আল্লাহর ইচ্ছায় প্রকৃতির অমোঘ নিয়মে ঋতুর পালাবদল ঘটে। কনকনে হিমেল হাওয়া ও তীব্র শৈত্যপ্রবাহ দরিদ্র জনগোষ্ঠী-অধ্যুষিত অঞ্চলে বিরূপ প্রভাব ফেলে এবং মানুষের অসহায়ত্বকে প্রকট করে তোলে। তাই শীতার্ত হতদরিদ্র মানুষের প্রতি সমাজের সামর্থ্যবান ও বিত্তশালী ব্যক্তিদের সাহায্য ও সহানুভূতির হাত সম্প্রসারিত করে তাদের পর্যাপ্ত পরিমাণে শীতবস্ত্র বিতরণ করে তাদের পাশে এসে দাঁড়ানো দরকার।', 'Counsilor', 1, '2021-12-30', 'approved'),
(25, 'বয়স্ক ভাতা', ' দুস্থ ও স্বল্প উপার্জনক্ষম অথবা উপার্জনে অক্ষম বয়স্ক জনগোষ্ঠীর সামাজিক নিরাপত্তা বিধানে ও পরিবার ও সমাজে মর্যাদা বৃদ্ধির লক্ষ্যে ১৯৯৭-৯৮ অর্থ বছরে  ‘বয়স্কভাতা’ কর্মসূচি প্রবর্তন  করা হয়। প্রাথমিকভাবে দেশের সকল ইউনিয়ন পরিষদের প্রতিটি ওয়ার্ডে ৫ জন পুরুষ ও ৫ জন মহিলাসহ ১০ জন দরিদ্র বয়োজ্যেষ্ঠ ব্যক্তিকে প্রতিমাসে ১০০ টাকা হারে ভাতা প্রদানের আওতায় আনা হয়। পরবর্তীতে দেশের সকল পৌরসভা ও সিটিকর্পোরেশন এ কর্মসূচির আওতাভুক্ত করা হয়।', 'Counsilor', 1, '2021-12-30', 'approved'),
(28, 'খাদ্যে ভেজাল রোধে প্রতিকারের চেয়ে প্রতিরোধ জরুরি', 'নিরাপদ খাদ্য পাওয়া মানুষের মৌলিক মানবাধিকার। অনিরাপদ খাদ্য বড় ধরনের জনস্বাস্থ্য সমস্যা। হাসপাতালগুলোতে রোগী বেড়ে যাওয়ার কারণ দূষিত ও ভেজাল তথা অনিরাপদ খাদ্য। মাটি দূষিত হলে সেই মাটি থেকে প্রাপ্ত খাদ্যের বিশুদ্ধ হওয়ার সুযোগ কম। আবার কীটনাশকের যথেচ্ছ ব্যবহারে ফসল অনিরাপদ হতে পারে। বিক্রেতার অসতর্কতা-অসততায়ও কোনো খাদ্য অনিরাপদ হতে পারে। নাগরিকরা সচেতন হলে খাদ্যে ভেজাল ও দূষণ প্রতিরোধ সম্ভব। যথানিয়মে রান্না কিংবা সংরক্ষণ না করলে নিরাপদ খাদ্যও অনিরাপদ হয়ে যেতে পারে। কোন খাদ্য নিরাপদ এবং কীভাবে নিরাপদ রাখতে হবে এসব বিষয়ে সম্যক জ্ঞান থাকলে এবং সংশ্লিষ্ট কর্তৃপক্ষের নির্দেশনা মেনে চললেই খাদ্যে ভেজাল ও দূষণ রোধ করা সম্ভব।', 'Counsilor', 1, '2022-01-03', 'approved'),
(29, 'শিশু-কিশোরদের শারিরীক এবং মানসিক বিকাশে পর্যাপ্ত পার্ক ও মাঠের দাবি', 'রাজধানীতে প্রায় ২ কোটি লোকের বসবাস। জনসংখ্যার দিক থেকে সর্বোচ্চ সীমা অতিক্রম করেছে এই শহর। মানুষের গায়ে গা লাগার মতো দশা। যার কারণে সুস্থ জীবনযাপনের সব সুবিধা দেয়া কর্তৃপক্ষের সম্ভব হয়ে উঠে না। সম্ভব হয় না আরো একটি কারণে তা হলো পরিকল্পনাহীন নগরায়ন। তারপরও নানা সীমাবদ্ধা তার মধ্যেও কিছু কিছু বিষয় আবশ্যিক হয়ে দাঁড়ায়। ঢাকা শহরে পার্ক বা বিনোদন কেন্দ্র তেমনি আবশ্যিক। মানুষের বেঁচে থাকার জন্য শুধু খাবারই যথেষ্ট নয়, বিনোদনেরও প্রয়োজন আছে।', 'Counsilor', 1, '2022-01-03', 'approved'),
(30, 'বিশুদ্ধ পানির অপর নাম সুস্থ জীবন', 'প্রয়োজনীয় বিশুদ্ধ পানি পানির অভাবে অনেক রকমের শারীরিক সমস্যা দেখা দিতে পারে। পানি পর্যাপ্ত পরিমাণে পান করলে আমাদের পিপাসা মেটানোর পাশাপাশি শরীরের পানিশূন্যতাও দূর হয়। ফলে শারীরিক ক্লান্তি দূর হয় এবং শক্তিও ফিরে আসে। পানি রক্ত ও রক্ত কণিকায় অক্সিজেন এবং অন্যান্য পুষ্টি উপাদান সরবরাহ করে', 'Counsilor', 1, '2022-01-03', 'approved'),
(31, 'বিশুদ্ধ পানির অপর নাম সুস্থ জীবন', 'প্রয়োজনীয় বিশুদ্ধ পানি পানির অভাবে অনেক রকমের শারীরিক সমস্যা দেখা দিতে পারে। পানি পর্যাপ্ত পরিমাণে পান করলে আমাদের পিপাসা মেটানোর পাশাপাশি শরীরের পানিশূন্যতাও দূর হয়। ফলে শারীরিক ক্লান্তি দূর হয় এবং শক্তিও ফিরে আসে। পানি রক্ত ও রক্ত কণিকায় অক্সিজেন এবং অন্যান্য পুষ্টি উপাদান সরবরাহ করে', 'Counsilor', 1, '2022-01-03', ''),
(32, 'শহুরে- আমার আধুনিক শহর', 'প্রকৃতির সর্বত্র আমাদের মাথা গলে না| এমন অনেক স্থান আছে যেখানে বুদ্ধি অন্ধকারে হাতড়াইয়া মরে, হৃদয় আপন আলোকে পথ দেখায়', 'mayor', 0, '2022-01-03', 'approved'),
(33, 'দরিদ্র মা’র জন্য মাতৃত্বকাল ভাতা প্রদান কর্মসূচি', 'ইউনিয়ন কমিটি কর্তৃক মাইকিং করে নির্দিষ্ট তারিখে সম্ভাব্য প্রার্থীদের উপস্থিত হবার জন্য প্রচারণা চালানো হয় এবং গর্ভধারিণী মা সম্পর্কে স্থানীয়ভাবে জরিপ এবং তথ্যানুসন্ধান করা হয়। স্কুল, কলেজ/মাদ্রাসার প্রধান, ইমাম, স্থানীয় কাজী এবং ইউনিয়ন ভূমি সহকারীদের নিকট হতে বয়স, বিবাহ, সন্তানসংখ্যা, মাসিক আয়, সম্পদের মালিকানাসংক্রান্ত সুনির্দিষ্ট তথ্য সংগ্রহ ক', 'Counsilor', 10, '2022-01-15', 'approved'),
(34, 'সবাই সচেতন হলে ডেঙ্গু প্রতিরোধ সম্ভব', 'করোনাভাইরাসের ঊর্ধ্বমুখী সংক্রমণের মধ্যেই রাজধানীতে ডেঙ্গু রোগীর সংখ্যা আশঙ্কাজনক হারে বাড়তে শুরু করেছে। ডেঙ্গু জ্বরে যখন সারা দেশ কাঁপছে তখন ডেঙ্গুবাহী এডিস মশা দমনে উদ্যোগ নিল স্বেচ্ছাসেবী সংগঠন ‘মাঞ্জা’। সংগঠনের উদ্যোগে স্বেচ্ছাসেবকরা মশন নিধন ওষুধ ছিটাচ্ছেন রাজধানীর পুরান ঢাকার বিভিন্ন এলাকায়', 'Counsilor', 10, '2022-01-15', 'approved'),
(35, 'নগদ ও বিকাশে সরকারি ভাতা যাবে সরাসরি উপকারভোগীর হাতে', 'সমাজকল্যাণ মন্ত্রণালয়সহ মোবাইলে ভাতা পাঠানোর প্রক্রিয়ায় সংশ্লিষ্টদের ধন্যবাদ জানিয়ে শেখ হাসিনা বলেন, আমরা যেই সমস্ত ভাতাগুলো দিচ্ছি এটা যেন যথাযথ সঠিকভাবে যে মানুষটাকে আমরা দেব তার হাতে পৌঁছায়। মাঝে যেন আর কেউ না থাকে। তাদের অর্থটা তাদের হাতে যাবে। তাদের যেভাবে খুশি তারা সেভাবে ব্যবহার করবে। সেটুকু যেন আমরা করতে পারি। সেটার ব্যবস্থা দীর্ঘদিন ধরে আমরা চেষ্টা করে যাচ্ছিলাম। আমি ধন্যবাদ জানাচ্ছি সেই ব্যবস্থাটা আপনারা নিয়েছেন।', 'Counsilor', 10, '2022-01-15', ''),
(36, 'করোনাকালীন সময়ে গণপরিবহন ব্যবহারে সচেতনতা', 'করোনাভাইরাসের নানা ধরন রয়েছে। এর মধ্যে কোনো কোনোটি প্রাণঘাতী হয়ে উঠতে পারে। বর্তমানে চীন থেকে ছড়িয়ে পড়া নতুন করোনাভাইরাস তেমনই একটি ভাইরাস। এখন পর্যন্ত এই ভাইরাসের সংক্রমণের বা কোভিড-১৯ রোগের কোনো টিকা আবিষ্কৃত হয়নি। এই ভাইরাসের সংক্রমণ প্রতিরোধে ব্যক্তিগত সচেতনতার কোনো বিকল্প নেই।', 'Counsilor', 10, '2022-01-15', 'approved');
